// MyClass.h\n#pragma once\n\n#ifdef MYDLL_EXPORTS\n#define MYDLL_API __declspec(dllexport)\n#else\n#define MYDLL_API __declspec(dllimport)\n#endif\n\n#include <vector>\n\nclass MYDLL_API MyClass {\npublic:\n    MyClass();\n    void MyFunction(std::vector<int> data);\n};\n\n// MyClass.cpp\n#include "MyClass.h"\n#include <iostream>\n\nMyClass::MyClass() {\n    // Constructor implementation\n}\n\nvoid MyClass::MyFunction(std::vector<int> data) {\n    // Function implementation\n    for (int i : data) {\n        std::cout << i << " ";\n    }\n    std::cout << std::endl;\n}