#include <iostream>\n#include <fstream>\n\nint main() {\n    std::ifstream file("yourfile.txt");\n    if (!file.is_open()) {\n        std::cerr << "Unable to open file";\n        return 1;\n    }\n\n    char ch;\n    bool newLine = true;\n\n    while (file.get(ch)) {\n        if (ch == '\n') {\n            std::cout << "\n[NEW LINE]\n";\n            newLine = true;\n        } else {\n            if (newLine && ch != ' ') {\n                newLine = false;\n            }\n            std::cout << ch;\n        }\n    }\n\n    file.close();\n    return 0;\n}