#include <windows.h>\n#include <lm.h>\n#include <iostream>\n\nint main() {\n    LPUSER_MODALS_INFO_0 pBuf = NULL;\n    NET_API_STATUS nStatus;\n\n    nStatus = NetUserModalsGet(NULL, 0, (LPBYTE*)&pBuf);\n\n    if (nStatus == NERR_Success) {\n        std::wcout << L"Minimum Password Length: " << pBuf->usrmod0_min_passwd_len << std::endl;\n        std::wcout << L"Password History Length: " << pBuf->usrmod0_password_hist_len << std::endl;\n        std::wcout << L"Maximum Password Age: " << pBuf->usrmod0_max_passwd_age << std::endl;\n        std::wcout << L"Minimum Password Age: " << pBuf->usrmod0_min_passwd_age << std::endl;\n    } else {\n        std::wcout << L"NetUserModalsGet failed with error: " << nStatus << std::endl;\n    }\n\n    if (pBuf != NULL) {\n        NetApiBufferFree(pBuf);\n    }\n\n    return 0;\n}