#include <windows.h>\n#include <gdiplus.h>\nusing namespace Gdiplus;\n\nvoid BlendAlpha(Bitmap* source, Bitmap* alphaMask) {\n    // Ensure the dimensions of both images match\n    if (source->GetWidth() != alphaMask->GetWidth() || source->GetHeight() != alphaMask->GetHeight()) {\n        return;\n    }\n\n    for (UINT y = 0; y < source->GetHeight(); ++y) {\n        for (UINT x = 0; x < source->GetWidth(); ++x) {\n            Color srcColor, maskColor;\n            source->GetPixel(x, y, &srcColor);\n            alphaMask->GetPixel(x, y, &maskColor);\n\n            // Set the new alpha using the alpha value from the mask\n            Color newColor(srcColor.GetR(), srcColor.GetG(), srcColor.GetB(), maskColor.GetA());\n            source->SetPixel(x, y, newColor);\n        }\n    }\n}\n\nint main() {\n    // Initialize GDI+\n    GdiplusStartupInput gdiplusStartupInput;\n    ULONG_PTR gdiplusToken;\n    GdiplusStartup(&gdiplusToken, &gdiplusStartupInput, NULL);\n\n    // Load your images\n    Bitmap source(L"source.png");\n    Bitmap mask(L"mask.png");\n\n    // Blend the alpha\n    BlendAlpha(&source, &mask);\n\n    // Save the result\n    source.Save(L"output.png", &ImageFormatPNG);\n\n    // Shutdown GDI+\n    GdiplusShutdown(gdiplusToken);\n    return 0;\n}