// StringArrayDLL.cpp\n#include <windows.h>\n\n// Define the function to export\nextern "C" __declspec(dllexport) void __stdcall GetStringArray(BSTR* arr, int* length) {\n    // Define the strings to return\n    const wchar_t* strings[] = { L"Hello", L"World", L"From", L"C++" };\n    int count = sizeof(strings) / sizeof(strings[0]);\n\n    // Allocate memory for the BSTR array\n    for (int i = 0; i < count; ++i) {\n        arr[i] = SysAllocString(strings[i]);\n    }\n\n    // Set the length of the array\n    *length = count;\n}