// In your dialog or view header file, declare a CFont member variable\nCFont m_Font;\n\n// In your dialog or view implementation file\nvoid YourClass::SetListCtrlFont()\n{\n    // Ensure to release the font resources if already created\n    if (m_Font.GetSafeHandle() != NULL)\n    {\n        m_Font.DeleteObject();\n    }\n\n    // Create a new font\n    m_Font.CreatePointFont(100, _T("Courier New")); // Example for a fixed width font\n\n    // Set the font to the list control\n    CListCtrl* pListCtrl = (CListCtrl*)GetDlgItem(IDC_YOUR_LIST_CONTROL);\n    if (pListCtrl != NULL)\n    {\n        pListCtrl->SetFont(&m_Font);\n    }\n}\n\n// Then you can call SetListCtrlFont() wherever appropriate in your code, for example, in OnInitDialog or some other initialization function.\nBOOL YourClass::OnInitDialog()\n{\n    CDialogEx::OnInitDialog();\n    \n    // Call the function to set the font\n    SetListCtrlFont();\n\n    return TRUE;  // return TRUE unless you set the focus to a control\n}