// Connect the signal handler\nwidget->signal_button_press_event().connect(sigc::mem_fun(*this, &YourClass::on_button_press_event));\n\n// Define the signal handler in your class\nbool YourClass::on_button_press_event(GdkEventButton* event)\n{\n    if (event->type == GDK_BUTTON_PRESS && event->button == 1) {\n        // Handle single left click\n        return true; // Event has been handled\n    }\n    return false; // Event has not been handled\n}