#include <iostream>\n#include <fstream>\n#include <sstream>\n#include <vector>\n#include <string>\n\nstruct InventoryItem {\n    std::string description;\n    int quantity;\n    double wholesaleCost;\n    double retailCost;\n    std::string dateAdded;\n\n    std::string toCSV() const {\n        std::ostringstream oss;\n        oss << description << "," << quantity << "," << wholesaleCost << "," << retailCost << "," << dateAdded;\n        return oss.str();\n    }\n\n    static InventoryItem fromCSV(const std::string &line) {\n        std::istringstream iss(line);\n        InventoryItem item;\n        std::getline(iss, item.description, ',');\n        iss >> item.quantity;\n        iss.ignore(1);\n        iss >> item.wholesaleCost;\n        iss.ignore(1);\n        iss >> item.retailCost;\n        iss.ignore(1);\n        std::getline(iss, item.dateAdded);\n        return item;\n    }\n};