#include <iostream>\n#include <windows.h>\n\nvoid setCursorPosition(int x, int y) {\n    HANDLE hConsole = GetStdHandle(STD_OUTPUT_HANDLE);\n    COORD position = { static_cast<SHORT>(x), static_cast<SHORT>(y) };\n    SetConsoleCursorPosition(hConsole, position);\n}\n\nint main() {\n    // Initial output\n    std::cout << "Running... 0% complete" << std::endl;\n    std::cout << "Buffer size: 0 bytes" << std::endl;\n\n    // Simulate an update loop\n    for (int i = 1; i <= 100; ++i) {\n        // Move cursor back to the start of the first line\n        setCursorPosition(11, 0);\n        std::cout << i << "% complete";\n\n        // Move cursor back to the start of the second line\n        setCursorPosition(13, 1);\n        std::cout << i * 100 << " bytes";\n\n        // Simulate a delay\n        Sleep(100);\n    }\n\n    return 0;\n}