// MyLibrary.h\n   #ifndef MYLIBRARY_H\n   #define MYLIBRARY_H\n\n   #ifdef MYLIBRARY_EXPORTS\n   #define MYLIBRARY_API __declspec(dllexport)\n   #else\n   #define MYLIBRARY_API __declspec(dllimport)\n   #endif\n\n   MYLIBRARY_API void HelloWorld();\n\n   #endif // MYLIBRARY_H\n\n   // MyLibrary.cpp\n   #include "MyLibrary.h"\n   #include <iostream>\n\n   MYLIBRARY_API void HelloWorld() {\n       std::cout << "Hello, world!" << std::endl;\n   }