#include <sys/statvfs.h>\n#include <iostream>\n\nvoid GetClusterSize(const char* path) {\n    struct statvfs stat;\n\n    if (statvfs(path, &stat) == 0) {\n        unsigned long bytesPerCluster = stat.f_frsize;\n        std::cout << "Bytes per cluster: " << bytesPerCluster << std::endl;\n    } else {\n        std::cerr << "Error getting file system stats" << std::endl;\n    }\n}\n\nint main() {\n    GetClusterSize("/");\n    return 0;\n}