#include <windows.h>\n#include <iostream>\n\nbool CheckRegistryValueExists(HKEY hKeyRoot, LPCSTR subKey, LPCSTR valueName) {\n    HKEY hKey;\n    LONG lResult = RegOpenKeyExA(hKeyRoot, subKey, 0, KEY_READ, &hKey);\n\n    if (lResult != ERROR_SUCCESS) {\n        return false;\n    }\n\n    DWORD dwType = 0;\n    DWORD dwDataSize = 0;\n    lResult = RegQueryValueExA(hKey, valueName, nullptr, &dwType, nullptr, &dwDataSize);\n\n    RegCloseKey(hKey);\n\n    return (lResult == ERROR_SUCCESS);\n}\n\nint main() {\n    const char* subKey = "SOFTWARE\\YourCompany\\YourApp";\n    const char* valueName = "YourValue";\n    \n    if (CheckRegistryValueExists(HKEY_CURRENT_USER, subKey, valueName)) {\n        std::cout << "Registry value exists." << std::endl;\n    } else {\n        std::cout << "Registry value does not exist." << std::endl;\n    }\n\n    return 0;\n}