#include <atomic>\n#include <thread>\n#include <iostream>\n\nstd::atomic<int> value(0);\n\nvoid writer() {\n    for (int i = 0; i < 1000; ++i) {\n        value.fetch_add(1, std::memory_order_relaxed);\n    }\n}\n\nvoid reader() {\n    for (int i = 0; i < 1000; ++i) {\n        int v = value.load(std::memory_order_relaxed);\n        std::cout << v << std::endl;\n    }\n}\n\nint main() {\n    std::thread t1(writer);\n    std::thread t2(reader);\n\n    t1.join();\n    t2.join();\n\n    return 0;\n}