#include <windows.h>\n#include <connmgr.h>\n\nvoid CloseAllGPRSConnections()\n{\n    HANDLE hConn = NULL;\n    CONNMGR_CONNECTION_DETAILED_STATUS status;\n    DWORD dwStatusSize = sizeof(status);\n    DWORD dwResult = ConnMgrEnumConnectionDetail(0, &hConn, &status, &dwStatusSize);\n\n    while(dwResult == ERROR_SUCCESS)\n    {\n        if (status.dwType == CM_CONNTYPE_GPRS)\n        {\n            ConnMgrReleaseConnection(hConn, TRUE);\n        }\n\n        dwResult = ConnMgrEnumConnectionDetail(++hConn, NULL, &status, &dwStatusSize);\n    }\n}\n\nint main()\n{\n    CloseAllGPRSConnections();\n    return 0;\n}