#include <iostream>\n#include <ctime>\n\n// Function to get the week number of a given date\nint getWeekNumber(int year, int month, int day) {\n    std::tm time_in = { 0, 0, 0, day, month - 1, year - 1900 };\n    std::time_t time_temp = std::mktime(&time_in);\n    \n    // Note: std::localtime is not thread-safe. Use std::localtime_r if thread safety is needed.\n    const std::tm *time_out = std::localtime(&time_temp);\n\n    // Calculate the week number\n    char buffer[3];\n    std::strftime(buffer, 3, "%U", time_out);\n    return std::stoi(buffer);\n}\n\nint main() {\n    int year = 2008;\n    int month = 1; // January\n    int day = 10;\n\n    int weekNumber = getWeekNumber(year, month, day);\n    std::cout << "The week number for " << year << "-" << month << "-" << day << " is: " << weekNumber << std::endl;\n\n    return 0;\n}