#include <iostream>\n#include <vector>\n\n// Example struct\nstruct Example {\n    int a;\n    float b;\n    std::string c;\n};\n\n// Function to print the contents of the struct\nvoid printExample(const Example& ex) {\n    std::cout << "Example { a: " << ex.a << ", b: " << ex.b << ", c: " << ex.c << " }" << std::endl;\n}\n\n// Function to print the contents of an integer array\nvoid printArray(const std::vector<int>& arr) {\n    std::cout << "Array [ ";\n    for (const int& elem : arr) {\n        std::cout << elem << " ";\n    }\n    std::cout << "]" << std::endl;\n}\n\nint main() {\n    // Create an instance of Example\n    Example ex = {1, 2.34, "hello"};\n    printExample(ex);\n\n    // Create an array of integers\n    std::vector<int> arr = {1, 2, 3, 4, 5};\n    printArray(arr);\n\n    return 0;\n}