#include <stdexcept>\n#include <string>\n\nvoid someFunction(int argument) {\n    if (argument < 0) {\n        throw std::invalid_argument("Argument must be non-negative");\n    }\n    // Function logic here\n}\n\nint main() {\n    try {\n        someFunction(-1);\n    } catch (const std::invalid_argument& e) {\n        // Handle the exception\n        std::cerr << "Caught exception: " << e.what() << std::endl;\n    }\n    return 0;\n}