#include <jni.h>\n#include <d3d9.h>\n\n// Function to initialize DirectX (example)\nextern "C"\nJNIEXPORT jlong JNICALL Java_DirectXWrapper_initDirectX(JNIEnv* env, jobject obj) {\n    IDirect3D9* d3d = Direct3DCreate9(D3D_SDK_VERSION);\n    return reinterpret_cast<jlong>(d3d);\n}\n\n// Function to release DirectX (example)\nextern "C"\nJNIEXPORT void JNICALL Java_DirectXWrapper_releaseDirectX(JNIEnv* env, jobject obj, jlong ptr) {\n    IDirect3D9* d3d = reinterpret_cast<IDirect3D9*>(ptr);\n    if (d3d) {\n        d3d->Release();\n    }\n}