#include <windows.h>\n#include <tchar.h>\n#include <stdio.h>\n\n#define BUFSIZE 100\n\nint _tmain(int argc, TCHAR *argv[])\n{\n    BOOL bFlag;\n    TCHAR Buf[BUFSIZE];     // temporary buffer for volume name\n\n    if (argc != 3) \n    {\n        _tprintf(TEXT("Usage: %s <mount_point> <volume>\n"), argv[0]);\n        _tprintf(TEXT("For example, \"%s c:\\mnt\\fdrive\\ f:\\\"\n"), argv[0]);\n        return -1;\n    }\n\n    // We should do some error checking on the inputs. Make sure\n    // there are colons and backslashes in the right places, etc. \n\n    bFlag = GetVolumeNameForVolumeMountPoint(\n                argv[2], // input volume mount point or directory\n                Buf, // output volume name buffer\n                BUFSIZE  // size of volume name buffer\n            );\n\n    if (bFlag != TRUE) \n    {\n        _tprintf(TEXT("Retrieving volume name for %s failed.\n"), argv[2]);\n        return -2;\n    }\n\n    _tprintf(TEXT("Volume name of %s is %s\n"), argv[2], Buf);\n    bFlag = SetVolumeMountPoint(\n                argv[1], // mount point\n                Buf  // volume to be mounted\n            );\n\n    if (!bFlag)\n        _tprintf(TEXT("Attempt to mount %s at %s failed.\n"), argv[2], argv[1]);\n\n    return (bFlag);\n}