#include <vector>\n#include <iostream>\n\nint main() {\n    std::vector<int*> v1 = { new int(1), new int(2), new int(3) };\n    std::vector<const int*> v2;\n\n    // Perform element-wise assignment\n    for (int* ptr : v1) {\n        v2.push_back(ptr);\n    }\n\n    // Verify the contents of v2\n    for (const int* ptr : v2) {\n        std::cout << *ptr << " ";\n    }\n\n    // Clean up allocated memory\n    for (int* ptr : v1) {\n        delete ptr;\n    }\n\n    return 0;\n}