#include <cstdio>\n#include <iostream>\n\nvoid opentest()\n{\n    FILE *fp = fopen("dqmapt.mp", "rb");\n    if (fp)\n    {\n        int buffer = 0;\n        size_t bytesRead = fread(&buffer, sizeof(int), 1, fp);\n        \n        if (bytesRead == 1)\n        {\n            printf("Read integer: %d\n", buffer);\n        }\n        else\n        {\n            std::cerr << "Failed to read an integer from the file.\n";\n        }\n        \n        fclose(fp);\n    }\n    else\n    {\n        std::cerr << "Failed to open the file.\n";\n    }\n}\n\nint main()\n{\n    opentest();\n    return 0;\n}