#include <wx/wx.h>\n#include <wx/msw/ole/activex.h>\n#include <exdisp.h>\n\n// Function to navigate with custom user agent\nvoid NavigateWithUserAgent(wxActiveXContainer* ieCtrl, const wxString& url, const wxString& userAgent)\n{\n    wxString headers = wxString::Format("User-Agent: %s\r\n", userAgent);\n    wxVariant vURL(url);\n    wxVariant vHeaders(headers);\n    wxVariant vEmpty(wxEmptyString);\n\n    ieCtrl->GetDispatch()->Invoke(wxDISPID_NAVIGATE, wxDISPATCH_METHOD, NULL,\n                                  { &vURL, &vEmpty, &vEmpty, &vEmpty, &vHeaders });\n}\n\n// Create ActiveX control and navigate\nvoid CreateIEControl(wxWindow* parent)\n{\n    wxActiveXContainer* ieCtrl = new wxActiveXContainer(parent, IID_IWebBrowser2);\n    wxString url = "http://www.example.com";\n    wxString userAgent = "MyCustomUserAgent/1.0";\n\n    NavigateWithUserAgent(ieCtrl, url, userAgent);\n}\n\nclass MyApp : public wxApp\n{\npublic:\n    virtual bool OnInit()\n    {\n        wxFrame* frame = new wxFrame(NULL, wxID_ANY, "IE User Agent Example");\n        CreateIEControl(frame);\n        frame->Show();\n        return true;\n    }\n};\n\nwxIMPLEMENT_APP(MyApp);