#include <windows.h>\n#include <comdef.h>\n#include <iostream>\n\n// Initialize COM.\nHRESULT hr = CoInitialize(NULL);\nif (FAILED(hr)) {\n    std::cerr << "Failed to initialize COM library." << std::endl;\n    return -1;\n}\n\nCLSID clsid;\nhr = CLSIDFromProgID(L"YourProgIDHere", &clsid);\nif (FAILED(hr)) {\n    std::cerr << "DLL is not registered." << std::endl;\n    // Uninitialize COM.\n    CoUninitialize();\n    return -1;\n}\n\nIGetTestPtr ptest;\nhr = ptest.CreateInstance(__uuidof(tester));\nif (FAILED(hr)) {\n    std::cerr << "Failed to create instance of COM object." << std::endl;\n    // Uninitialize COM.\n    CoUninitialize();\n    return -1;\n}\n\n// Use method from the dll\n\n// Uninitialize COM.\nCoUninitialize();