#include <regex>\n#include <string>\n#include <iostream>\n\nbool validateHostnameOrIP(const std::string& hostname) {\n    // Regular expression for validating an IP address or hostname\n    const std::regex pattern(\n        R"(^(([a-zA-Z0-9-_]+\.)*[a-zA-Z0-9-_]+\.[a-zA-Z]{2,}|(\d{1,3}\.){3}\d{1,3})$)"\n    );\n    return std::regex_match(hostname, pattern);\n}\n\nbool validatePort(const std::string& portStr) {\n    try {\n        int port = std::stoi(portStr);\n        return port > 0 && port <= 65535;\n    } catch (const std::exception&) {\n        return false;\n    }\n}\n\nint main() {\n    std::string hostname = "example.com";\n    std::string port = "8080";\n\n    if (validateHostnameOrIP(hostname) && validatePort(port)) {\n        std::cout << "Valid hostname and port." << std::endl;\n    } else {\n        std::cout << "Invalid hostname or port." << std::endl;\n    }\n\n    return 0;\n}