#include <QSqlDatabase>\n#include <QSqlQuery>\n#include <QVariant>\n\nvoid insertNullValue() {\n    QSqlDatabase db = QSqlDatabase::addDatabase("QSQLITE");\n    db.setDatabaseName("mydatabase.db");\n\n    if (!db.open()) {\n        // Handle error\n        return;\n    }\n\n    QSqlQuery query;\n    query.prepare("INSERT INTO mytable (mycolumn) VALUES (:value)");\n    query.bindValue(":value", QVariant(QVariant::String));\n\n    if (!query.exec()) {\n        // Handle query execution error\n    }\n\n    db.close();\n}