#include <iostream>\n#include <string>\n\nclass Mul {\npublic:\n    Mul(int a, int b) : a(a), b(b) {}\n\n    operator int() const {\n        return a * b;\n    }\n\n    operator std::string() const {\n        return std::string(b, '0' + a);\n    }\n\nprivate:\n    int a, b;\n};\n\nint main() {\n    Mul mul(6, 3);\n\n    int n = mul;               // n = 18\n    std::string s = mul;       // s = "666"\n\n    std::cout << "n = " << n << std::endl;\n    std::cout << "s = " << s << std::endl;\n\n    return 0;\n}