#include <iostream>\n#include <vector>\n#include <string>\n#include <algorithm>\n\nclass CFileInfo  \n{  \npublic:  \n    std::string m_PackLine;  \n    std::string m_FileDateTime;  \n    int m_NumDownloads;  \n};  \n\n// Comparator function to compare CFileInfo objects by m_FileDateTime\nbool compareByDateTime(const CFileInfo &a, const CFileInfo &b) {\n    return a.m_FileDateTime < b.m_FileDateTime;\n}\n\nint main()  \n{  \n    CFileInfo packInfo;  \n    std::vector<CFileInfo> unsortedFiles; \n\n    // Adding sample data\n    packInfo.m_PackLine = "Sample Line 1";  \n    packInfo.m_FileDateTime = "06/22/2008 04:34";  \n    packInfo.m_NumDownloads = 0;  \n    unsortedFiles.push_back(packInfo);  \n\n    packInfo.m_PackLine = "Sample Line 2";  \n    packInfo.m_FileDateTime = "12/05/2007 14:54";  \n    packInfo.m_NumDownloads = 1;  \n    unsortedFiles.push_back(packInfo);  \n\n    // Sort the vector using std::sort and the custom comparator\n    std::sort(unsortedFiles.begin(), unsortedFiles.end(), compareByDateTime);\n\n    // Print sorted results\n    for (const auto &file : unsortedFiles) {  \n        std::cout << file.m_PackLine << " " << file.m_FileDateTime << std::endl;  \n    }  \n\n    return 0;\n}