#include <vector>\n\nstruct Fred {\n    int lastElementInSet;\n    // Other members...\n};\n\nstd::vector<Fred> gFred;\n\nvoid processFred() {\n    // init gFred\n\n    Fred* fred = &gFred[0];  // Use a pointer to the first element\n\n    size_t z = 0;\n    do {\n        fred = &gFred[z];  // Update the pointer to point to the current element\n\n        // Do odd processing with *fred\n\n        z++;\n    } while (fred->lastElementInSet == 0);\n}