#include <iostream>\n#include <occi.h>\n\nusing namespace oracle::occi;\nusing namespace std;\n\nint main() {\n    Environment *env = Environment::createEnvironment(Environment::DEFAULT);\n    Connection *conn = env->createConnection("username", "password", "database");\n\n    Statement *stmt = conn->createStatement("SELECT MAX(ORA_ROWSCN) AS MAX_SCN FROM your_table_name");\n    ResultSet *rs = stmt->executeQuery();\n\n    if (rs->next()) {\n        unsigned long max_scn = rs->getUInt(1);\n        cout << "MAX_SCN: " << max_scn << endl;\n\n        // Compare max_scn with the previously stored value to determine if the table has changed.\n        // Store the max_scn for future comparisons.\n    }\n\n    stmt->closeResultSet(rs);\n    conn->terminateStatement(stmt);\n    env->terminateConnection(conn);\n    Environment::terminateEnvironment(env);\n\n    return 0;\n}