#include <string>\n#include <map>\n\nstruct Foo\n{\n    Foo(std::string s) : foo_value(s) {}\n\n    bool operator<(const Foo& foo1) const { return foo_value < foo1.foo_value; }\n    bool operator>(const Foo& foo1) const { return foo_value > foo1.foo_value; }\n\n    std::string foo_value;\n};\n\nint main() {\n    std::map<Foo, int> myMap;\n    myMap[Foo("apple")] = 1;\n    myMap[Foo("banana")] = 2;\n\n    return 0;\n}