// Example EXE file code\n#include <windows.h>\n#include <iostream>\n\ntypedef void (*FuncType)();\n\nint main() {\n    HMODULE hDLL = LoadLibrary("example.dll");\n    if (hDLL != NULL) {\n        FuncType func = (FuncType)GetProcAddress(hDLL, "exampleFunction");\n        if (func != NULL) {\n            func();\n        } else {\n            std::cerr << "Function not found." << std::endl;\n        }\n        FreeLibrary(hDLL);\n    } else {\n        std::cerr << "DLL not found." << std::endl;\n    }\n    return 0;\n}