#include <iostream>\n     #include <cmath>\n\n     // Example: Simple harmonic oscillator simulation\n     void harmonicOscillator(double k, double m, double x0, double v0, double t_max, double dt) {\n         double t = 0;\n         double x = x0;\n         double v = v0;\n\n         while (t < t_max) {\n             double a = -k * x / m;\n             v += a * dt;\n             x += v * dt;\n             t += dt;\n\n             std::cout << "Time: " << t << " s, Position: " << x << " m" << std::endl;\n         }\n     }\n\n     int main() {\n         harmonicOscillator(1.0, 1.0, 1.0, 0.0, 10.0, 0.01);\n         return 0;\n     }