#include <iostream>\n#include <sstream>\n#include <iomanip>\n\nint main() {\n    // Decimal to Hexadecimal\n    int decimalNumber = 255;\n    std::stringstream ss;\n    ss << std::hex << decimalNumber;\n    std::string hexNumber = ss.str();\n    std::cout << "Decimal " << decimalNumber << " in hexadecimal is " << hexNumber << std::endl;\n\n    // Hexadecimal to Decimal\n    std::string hexStr = "FF";\n    int decimalFromHex = std::stoi(hexStr, nullptr, 16);\n    std::cout << "Hexadecimal " << hexStr << " in decimal is " << decimalFromHex << std::endl;\n\n    // Decimal to Binary\n    int binaryNumber = 255;\n    std::cout << "Decimal " << binaryNumber << " in binary is " << std::bitset<8>(binaryNumber) << std::endl;\n\n    // Binary to Decimal\n    std::string binaryStr = "11111111";\n    int decimalFromBinary = std::stoi(binaryStr, nullptr, 2);\n    std::cout << "Binary " << binaryStr << " in decimal is " << decimalFromBinary << std::endl;\n\n    return 0;\n}