// Define pins for shift registers\nconst int dataPin = 2;   // DS\nconst int clockPin = 4;  // SH_CP\nconst int latchPin = 7;  // ST_CP\n\nvoid setup() {\n  // Set pin modes\n  pinMode(dataPin, OUTPUT);\n  pinMode(clockPin, OUTPUT);\n  pinMode(latchPin, OUTPUT);\n\n  // Initialize digital pins 3 to 11 as outputs for source channels\n  for (int i = 3; i <= 11; i++) {\n    pinMode(i, OUTPUT);\n    digitalWrite(i, LOW); // Turn off all source channels initially\n  }\n\n  // Example: Turn on the first 8 LEDs in sequence\n  for (int i = 0; i < 8; i++) {\n    digitalWrite(3 + i, HIGH);  // Turn on source channel\n    sendToShiftRegisters(1 << i); // Turn on corresponding sink channel\n    delay(500);  // Wait for a bit\n    digitalWrite(3 + i, LOW);   // Turn off source channel\n  }\n}\n\n// Function to send data to the shift registers\nvoid sendToShiftRegisters(byte data) {\n  // Set latch pin to LOW to begin sending data\n  digitalWrite(latchPin, LOW);\n\n  // Shift out the data\n  shiftOut(dataPin, clockPin, MSBFIRST, data);\n\n  // Set latch pin to HIGH to latch the data\n  digitalWrite(latchPin, HIGH);\n}\n\nvoid loop() {\n  // You can add more control logic here\n}