#include <windows.h>\n   #include <string>\n\n   int main() {\n       // Get the current PATH\n       char oldPath[32767];\n       GetEnvironmentVariable("PATH", oldPath, 32767);\n       \n       // Set the new PATH\n       std::string newPath = std::string(oldPath) + ";C:\\path\\to\\your\\dll";\n       SetEnvironmentVariable("PATH", newPath.c_str());\n\n       // Now you can load your DLL or use its functions\n       // ...\n\n       // Optionally, reset the PATH after use\n       SetEnvironmentVariable("PATH", oldPath);\n\n       return 0;\n   }