#include <windows.h>\n#include <aclapi.h>\n#include <sddl.h>\n\nvoid SetFolderAclWithoutPropagation(const std::wstring& folderPath, const std::wstring& sddlString) {\n    PSECURITY_DESCRIPTOR pSD = NULL;\n    if (ConvertStringSecurityDescriptorToSecurityDescriptorW(sddlString.c_str(), SDDL_REVISION_1, &pSD, NULL)) {\n        DWORD result = SetNamedSecurityInfoW(\n            const_cast<LPWSTR>(folderPath.c_str()), // Target folder\n            SE_FILE_OBJECT,                         // Object type is a file (folder)\n            DACL_SECURITY_INFORMATION |             // Set DACL\n            PROTECTED_DACL_SECURITY_INFORMATION,    // Prevent inheritance from parent\n            NULL,                                   // Owner SID\n            NULL,                                   // Group SID\n            pSD,                                    // DACL\n            NULL                                    // SACL\n        );\n\n        if (result != ERROR_SUCCESS) {\n            wprintf(L"SetNamedSecurityInfo failed: %lu\n", result);\n        }\n\n        LocalFree(pSD);\n    } else {\n        wprintf(L"ConvertStringSecurityDescriptorToSecurityDescriptor failed: %lu\n", GetLastError());\n    }\n}\n\nint main() {\n    std::wstring folderPath = L"C:\\YourFolder";\n    std::wstring sddlString = L"D:(A;;FA;;;BA)"; // Example SDDL string, modify as needed\n\n    SetFolderAclWithoutPropagation(folderPath, sddlString);\n\n    return 0;\n}