#include <iostream>\n\nclass A {\npublic:\n    ~A() { std::cout << "Destroying A\n"; }\n};\n\nclass B {\npublic:\n    ~B() { std::cout << "Destroying B\n"; }\n};\n\nA& getAInstance() {\n    static A instance;\n    return instance;\n}\n\nB& getBInstance() {\n    static B instance;\n    return instance;\n}\n\nint main() {\n    // Accessing the instances, forcing their construction\n    getAInstance();\n    getBInstance();\n    \n    // The destruction order will be:\n    // 1. B's instance\n    // 2. A's instance\n}