#include <vector>\n#include <iostream>\n\n// Function to compute the index in a 1D array representing a 2D matrix\ninline size_t index(size_t i, size_t j, size_t n) {\n    return i + n * j;\n}\n\nint main() {\n    size_t n = 100; // matrix size (n x n)\n    \n    // Allocate a flat vector to represent the matrix\n    std::vector<double> matrix(n * n, 0.0);\n\n    // Access elements using the index function\n    size_t i = 1, j = 2;\n    matrix[index(i, j, n)] = 3.14;\n    \n    // Print the element to verify\n    std::cout << "Element at (" << i << ", " << j << ") is: " << matrix[index(i, j, n)] << std::endl;\n\n    return 0;\n}