#include <iostream>\n#include <windows.h>\n\nclass Console {\npublic:\n    static void Print(const std::string &message) {\n        SetConsoleTextAttribute(GetStdHandle(STD_OUTPUT_HANDLE), 15); // White\n        std::cout << message;\n    }\n\n    static void Warning(const std::string &message) {\n        SetConsoleTextAttribute(GetStdHandle(STD_OUTPUT_HANDLE), 14); // Yellow\n        std::cout << message;\n        ResetColor();\n    }\n\n    static void Error(const std::string &message) {\n        SetConsoleTextAttribute(GetStdHandle(STD_OUTPUT_HANDLE), 12); // Red\n        std::cout << message;\n        ResetColor();\n    }\n\nprivate:\n    static void ResetColor() {\n        SetConsoleTextAttribute(GetStdHandle(STD_OUTPUT_HANDLE), 15); // Reset to white\n    }\n};\n\nint main() {\n    Console::Print("This is a non-coloured message\n");\n    Console::Warning("This is a YELLOW warning message\n");\n    Console::Error("This is a RED error message\n");\n    return 0;\n}