#include <windows.h>\n\n// Function to find the main window and send a button click message\nvoid FakeButtonClick()\n{\n    // Find the main window of the target application\n    HWND hwndMain = FindWindow(NULL, L"Title of the Window");\n    if (hwndMain == NULL) {\n        MessageBox(NULL, L"Main window not found!", L"Error", MB_ICONERROR);\n        return;\n    }\n\n    // Find the button within the main window (use button's ID or handle)\n    HWND hwndButton = FindWindowEx(hwndMain, NULL, L"Button", L"Update");\n    if (hwndButton == NULL) {\n        MessageBox(NULL, L"Button not found!", L"Error", MB_ICONERROR);\n        return;\n    }\n\n    // Send a WM_COMMAND message to simulate a button click\n    SendMessage(hwndMain, WM_COMMAND, MAKEWPARAM(GetDlgCtrlID(hwndButton), BN_CLICKED), (LPARAM)hwndButton);\n}\n\nint main()\n{\n    // Call the function to fake the button click\n    FakeButtonClick();\n    return 0;\n}