#include <iostream>\n#include <sqlite3.h>\n#include <vector>\n\n// Define a simple C++ object\nstruct Person {\n    int id;\n    std::string name;\n    int age;\n};\n\n// Function to create an in-memory SQLite database and populate it with data\nvoid createAndPopulateDatabase(sqlite3* db, const std::vector<Person>& people) {\n    char* errMsg = 0;\n\n    // Create a table\n    const char* createTableSQL = "CREATE TABLE Person (ID INT, Name TEXT, Age INT);";\n    if (sqlite3_exec(db, createTableSQL, 0, 0, &errMsg) != SQLITE_OK) {\n        std::cerr << "SQL error: " << errMsg << std::endl;\n        sqlite3_free(errMsg);\n        return;\n    }\n\n    // Insert data into the table\n    for (const auto& person : people) {\n        std::string insertSQL = "INSERT INTO Person (ID, Name, Age) VALUES (" +\n            std::to_string(person.id) + ", '" + person.name + "', " + std::to_string(person.age) + ");";\n        if (sqlite3_exec(db, insertSQL.c_str(), 0, 0, &errMsg) != SQLITE_OK) {\n            std::cerr << "SQL error: " << errMsg << std::endl;\n            sqlite3_free(errMsg);\n            return;\n        }\n    }\n}\n\n// Function to query the database and print results\nvoid queryDatabase(sqlite3* db, const std::string& query) {\n    sqlite3_stmt* stmt;\n    if (sqlite3_prepare_v2(db, query.c_str(), -1, &stmt, 0) != SQLITE_OK) {\n        std::cerr << "Failed to prepare statement" << std::endl;\n        return;\n    }\n\n    while (sqlite3_step(stmt) == SQLITE_ROW) {\n        int id = sqlite3_column_int(stmt, 0);\n        const unsigned char* name = sqlite3_column_text(stmt, 1);\n        int age = sqlite3_column_int(stmt, 2);\n        std::cout << "ID: " << id << ", Name: " << name << ", Age: " << age << std::endl;\n    }\n\n    sqlite3_finalize(stmt);\n}\n\nint main() {\n    sqlite3* db;\n    \n    // Create an in-memory database\n    if (sqlite3_open(":memory:", &db)) {\n        std::cerr << "Can't open database: " << sqlite3_errmsg(db) << std::endl;\n        return 1;\n    }\n\n    std::vector<Person> people = {\n        {1, "Alice", 30},\n        {2, "Bob", 25},\n        {3, "Charlie", 35}\n    };\n\n    createAndPopulateDatabase(db, people);\n    \n    // Query the database\n    queryDatabase(db, "SELECT * FROM Person WHERE Age > 28;");\n    \n    // Close the database\n    sqlite3_close(db);\n\n    return 0;\n}