#include <windows.h>\n#include <iostream>\n#include <string>\n\nvoid GetFileAttributes(const std::wstring& filePath) {\n    WIN32_FILE_ATTRIBUTE_DATA fileInfo;\n\n    if (GetFileAttributesEx(filePath.c_str(), GetFileExInfoStandard, &fileInfo)) {\n        // File size\n        LARGE_INTEGER fileSize;\n        fileSize.HighPart = fileInfo.nFileSizeHigh;\n        fileSize.LowPart = fileInfo.nFileSizeLow;\n        std::wcout << L"File Size: " << fileSize.QuadPart << L" bytes\n";\n\n        // File creation time\n        FILETIME ftCreate = fileInfo.ftCreationTime;\n        SYSTEMTIME stUTC, stLocal;\n        FileTimeToSystemTime(&ftCreate, &stUTC);\n        SystemTimeToTzSpecificLocalTime(NULL, &stUTC, &stLocal);\n        std::wcout << L"Creation Time: " << stLocal.wDay << L"/" << stLocal.wMonth << L"/" << stLocal.wYear << L" "\n                   << stLocal.wHour << L":" << stLocal.wMinute << L":" << stLocal.wSecond << L"\n";\n\n        // File last access time\n        FILETIME ftAccess = fileInfo.ftLastAccessTime;\n        FileTimeToSystemTime(&ftAccess, &stUTC);\n        SystemTimeToTzSpecificLocalTime(NULL, &stUTC, &stLocal);\n        std::wcout << L"Last Access Time: " << stLocal.wDay << L"/" << stLocal.wMonth << L"/" << stLocal.wYear << L" "\n                   << stLocal.wHour << L":" << stLocal.wMinute << L":" << stLocal.wSecond << L"\n";\n\n        // File last write time\n        FILETIME ftWrite = fileInfo.ftLastWriteTime;\n        FileTimeToSystemTime(&ftWrite, &stUTC);\n        SystemTimeToTzSpecificLocalTime(NULL, &stUTC, &stLocal);\n        std::wcout << L"Last Write Time: " << stLocal.wDay << L"/" << stLocal.wMonth << L"/" << stLocal.wYear << L" "\n                   << stLocal.wHour << L":" << stLocal.wMinute << L":" << stLocal.wSecond << L"\n";\n    } else {\n        std::wcerr << L"GetFileAttributesEx failed with error: " << GetLastError() << L"\n";\n    }\n}\n\nint main() {\n    std::wstring filePath = L"C:\\path\\to\\your\\file.txt";\n    GetFileAttributes(filePath);\n    return 0;\n}