#include <iostream>\n#include <vector>\n#include <algorithm>\n\nint main() {\n    std::vector<std::vector<int>> v;\n\n    // Fill the vector with some data\n    v.push_back({1, 2, 3});\n    v.push_back({1, 2, 3, 4});\n    v.push_back({1, 2});\n\n    auto largest = std::max_element(v.begin(), v.end(), \n        [](const std::vector<int>& a, const std::vector<int>& b) {\n            return a.size() < b.size();\n        });\n\n    if (largest != v.end()) {\n        std::cout << "The largest container has size: " << largest->size() << std::endl;\n    }\n\n    return 0;\n}