#include <windows.h>\n\nBOOL CopyFileExample(LPCTSTR lpExistingFileName, LPCTSTR lpNewFileName)\n{\n    // Use the CopyFile function to copy the file\n    BOOL bSuccess = CopyFile(lpExistingFileName, lpNewFileName, FALSE);\n    if (!bSuccess)\n    {\n        // Handle the error\n        DWORD dwError = GetLastError();\n        // You can use dwError to determine the cause of the failure\n    }\n    return bSuccess;\n}