#include <windows.h>\n#include <objbase.h>\n\nint main() {\n    // Initialize COM library\n    HRESULT hr = CoInitialize(NULL);\n    if (FAILED(hr)) {\n        // Handle error\n        return hr;\n    }\n\n    // Load the DLL using CoLoadLibrary\n    HINSTANCE hInst = NULL;\n    hr = CoLoadLibrary(L"YourDLL.dll", TRUE, &hInst);\n    if (FAILED(hr)) {\n        // Handle error\n        CoUninitialize();\n        return hr;\n    }\n\n    // Use the DLL (e.g., call functions, create COM objects, etc.)\n    \n    // When done, free the loaded library\n    CoFreeLibrary(hInst);\n\n    // Uninitialize COM library\n    CoUninitialize();\n\n    return 0;\n}