#include <iostream>\n#include <fstream>\n#include <string>\n#include <codecvt>\n#include <locale>\n\nint main() {\n    // Sample UTF-8 string\n    std::string utf8_string = u8"こんにちは世界";\n\n    // Write UTF-8 string to file\n    std::ofstream utf8_file("output.txt");\n    utf8_file << utf8_string;\n    utf8_file.close();\n\n    // Read UTF-8 string from file\n    std::ifstream input_file("output.txt");\n    std::string read_string((std::istreambuf_iterator<char>(input_file)),\n                             std::istreambuf_iterator<char>());\n    input_file.close();\n\n    std::cout << "Read from file: " << read_string << std::endl;\n\n    // Convert UTF-8 to UTF-16\n    std::wstring_convert<std::codecvt_utf8_utf16<char16_t>, char16_t> convert;\n    std::u16string utf16_string = convert.from_bytes(utf8_string);\n\n    // Display UTF-16 string (requires appropriate handling for display purposes)\n    std::wcout << "UTF-16 string: " << reinterpret_cast<const wchar_t*>(utf16_string.c_str()) << std::endl;\n\n    return 0;\n}