// MyClass.h\n#ifdef MYDLL_EXPORTS\n#define DLL_API __declspec(dllexport)\n#else\n#define DLL_API __declspec(dllimport)\n#endif\n\nclass DLL_API MyClass {\npublic:\n    MyClass(); // Constructor must be exported\n    virtual ~MyClass(); // Destructor must be exported\n\n    virtual void virtualFunction(); // No need to export virtual functions\n    void nonVirtualFunction(); // This needs to be exported if called from outside the DLL\n\n    inline void inlineFunction() {\n        // Inline functions defined in the header do not need to be exported\n    }\n};