#include <vector>\n#include <algorithm>\n\n// Assuming the struct definitions and vector initializations as provided:\n\nstruct First {\n    int f1;\n    int f2;\n};\n\nstruct Second {\n    First s1;\n    int s2;\n};\n\nstd::vector<Second> secondVec;\n\nSecond sec;\nsec.s1 = First();\n\nsecondVec.push_back(sec);\nsecondVec.push_back(sec);\n\nstd::vector<First> firstVec;\nfirstVec.reserve(secondVec.size());\n\nstd::transform(secondVec.begin(), secondVec.end(), std::back_inserter(firstVec),\n               [](const Second& sec) -> First {\n                   return sec.s1;\n               });