#include <iostream>\n#include <random>\n\nint main() {\n    // Define a fixed seed\n    std::seed_seq seed{12345};\n\n    // Use the Mersenne Twister engine for consistent results\n    std::mt19937 engine(seed);\n\n    // Define a distribution\n    std::uniform_int_distribution<int> dist(1, 100);\n\n    // Generate random numbers\n    for (int i = 0; i < 10; ++i) {\n        std::cout << dist(engine) << " ";\n    }\n\n    std::cout << std::endl;\n\n    return 0;\n}