#include <iostream>\n#include <string>\n#include "php-serialize.h"\n\nint main() {\n    using namespace PHP;\n    \n    // Create a PHP associative array in C++\n    Array arr;\n    arr["name"] = "John Doe";\n    arr["age"] = 30;\n    arr["email"] = "johndoe@example.com";\n    \n    // Serialize the array\n    std::string serialized = serialize(arr);\n    std::cout << "Serialized PHP Data: " << serialized << std::endl;\n\n    // You can now pass this `serialized` string to PHP and unserialize it\n    return 0;\n}